import React from 'react';
import loginImg from "../../login.svg";
import { sha256 } from 'js-sha256';

export class Register extends React.Component {

    static username = ""
    static authID = ""
    static isRegistrating = false

    constructor(props) {
        super(props);
    }

    async handleRegister() {
        if (Register.username === "" || Register.authID === "") {
            console.log("Empty username and/or authID...")
            return
        }
        else if (Register.isRegistrating) {
            console.log("Registration in progress...")
            return
        }

        Register.isRegistrating = true;

        const RPrequestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 
                "username": Register.username,
                "authenticator_id": "AE52A450-7A4A-461E-9EEA-3AB71F019DAD"//Register.authID
            })
        };
        const RPresponse = await fetch('http://172.20.10.2:5050/register', RPrequestOptions);
        const RPdata = await RPresponse.json();
        console.log(RPdata)
        // TODO: Compare HTTP Origin with RP_ID
        //console.log(response.headers.get("Origin"));

        const rp_id = RPdata["rp"]["id"];
        const challenge = RPdata["challenge"];
        const timeout = RPdata["timeout"];

        var clientData = sha256.create();
        clientData.update(rp_id);
        clientData.update(challenge);

        const pollingRequestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 
                "authenticator_id": Register.authID,
                "rp_id": rp_id,
                "client_data": clientData.hex(),
                "timeout": timeout,
                "username": Register.username
            })
        };
        const pollingResponse = await fetch('http://172.20.10.2:5000/client/register', pollingRequestOptions);
        const pollingData = await pollingResponse.json();

        //Check response and act different based on repsonse from polling server
        console.log(pollingData);

        if (typeof pollingData === 'string') {
            Register.isRegistrating = false;
            return
        }

        const RPresponseOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 
                "credential_id": pollingData["credential_id"],
                "public_key": pollingData["public_key"],
                "client_data": pollingData["client_data"],
                "authenticator_id": pollingData["authenticator_id"],
                "signature": pollingData["signature"]
            })
        };
        const RPresponseResponse = await fetch('http://172.20.10.2:5050/authenticator/register', RPresponseOptions);
        const RPresponseData = await RPresponseResponse.json();
        console.log(RPresponseData)
        Register.isRegistrating = false;
    }

    render() {
        return (
            <div className="base-container" ref={this.props.containerRef}>
                <div className='header'>Register</div>
                <div className='content'>
                    <div className="image">
                        <img src={loginImg} />
                    </div>
                    <div className="form">
                        <div className="form-group">
                            <input type="text" name='username' onChange={(e) => Register.username=e.target.value} placeholder='Username'/>
                        </div>
                        <div className="form-group">
                            <input type="text" name='auth-id' onChange={(e) => Register.authID=e.target.value} placeholder='Auth-id'/>
                        </div>
                    </div>
                </div>
                <div className="footer">
                    <button type="button" className="btn" onClick={this.handleRegister}>
                        Register
                    </button>
                </div>
            </div>
        )
    }
}
