import React from 'react';
import loginImg from "../../login.svg";
import { sha256 } from 'js-sha256';

export class Login extends React.Component {

    static username = ""
    static isAuthenticating = false

    constructor(props) {
        super(props);
    }

    static changeErrorLabel(text) {
        document.getElementById('errorLabel').innerHTML = text;
    }

    async handleLogin() {

        document.getElementById('errorLabel').innerHTML = "";

        const username = Login.username;

        if (username === "") {
            console.log("Empty username");
            Login.changeErrorLabel("Please fill in a username");
            return
        }
        else if (Login.isAuthenticating) {
            console.log("Authentication in progress...")
            return
        }

        Login.isAuthenticating = true;

        const RPrequestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 
                "username": username
            })
        };
        const RPresponse = await fetch('http://192.168.39.177:5050/authenticate', RPrequestOptions);
        const RPdata = await RPresponse.json();
        console.log(RPdata)
        // TODO: Compare HTTP Origin with RP_ID
        //console.log(response.headers.get("Origin"));

        if (typeof RPdata === 'string') {
            Login.changeErrorLabel(RPdata)
            Login.isAuthenticating = false;
            return
        }

        const rp_id = RPdata["rp_id"];
        const challenge = RPdata["challenge"];
        const credID = RPdata["credential_id"];
        const timeout = RPdata["timeout"];
        const authID = RPdata["authenticator_id"];

        var clientData = sha256.create();
        clientData.update(rp_id);
        clientData.update(challenge);

        const pollingRequestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 
                "authenticator_id": authID,
                "rp_id": rp_id,
                "client_data": clientData.hex(),
                "timeout": timeout,
                "username": username,
                "credential_id": credID
            })
        };
        const pollingResponse = await fetch('http://192.168.39.177:5000/client/authenticate', pollingRequestOptions);
        const pollingData = await pollingResponse.json();

        //Check response and act different based on response from polling server
        console.log(pollingData);

        if (typeof pollingData === 'string') {
            Login.isAuthenticating = false;
            Login.changeErrorLabel(pollingData);
            return
        }

        const RPresponseOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 
                "username": username,
                "authenticator_data": pollingData["authenticator_data"],
                "w": pollingData["w"],
                "c": pollingData["c"],
                "z1": pollingData["z1"],
                "z2": pollingData["z2"],
                "rp_id": rp_id,
                "challenge": challenge
            })
        };
        const RPresponseResponse = await fetch('http://192.168.39.177:5050/authenticator/authenticate', RPresponseOptions);
        const RPresponseData = await RPresponseResponse.json();
        console.log(RPresponseData)

        if (typeof RPresponseData !== 'string') {
            const pollingResultOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ 
                    "authenticator_id": authID,
                    "username": username
                })
            };
            const pollingResultResponse = await fetch('http://192.168.39.177:5000/client/authenticate/failed', pollingResultOptions);
            const pollingResultData = await pollingResultResponse.json();
            console.log(pollingResultData);
            Login.changeErrorLabel(RPresponseData["msg"]);
        } else {
            Login.changeErrorLabel(RPresponseData);
        }
        Login.isAuthenticating = false;
    }

    render() {
        return (
            <div className="base-container" ref={this.props.containerRef}>
                <div className='header'>Login</div>
                <div className='content'>
                    <div className="image">
                        <img src={loginImg} />
                    </div>
                    <div className="form">
                        <div className="form-group">
                            <input type="text" name='username' onChange={(e) => Login.username=e.target.value} placeholder='Username'/>
                        </div>
                    </div>
                </div>
                <label id='errorLabel'></label>
                <div className="footer">
                    <button type="button" className="btn" onClick={this.handleLogin}>
                        Login
                    </button>
                </div>
            </div>
        )
    }
}
